/**
 * Defines Schema field
 * @param {*} type Field type
 */
export declare function SchemaField(type: any): PropertyDecorator;
/**
 * Defines static method or property
 */
export declare function Static(): (target: any, name: string, descriptor?: TypedPropertyDescriptor<any>) => void;
/**
 * Defines query method
 */
export declare function Query(): (target: any, name: string) => void;
/**
 * Defines instance method
 */
export declare function Instance(): (target: any, name: string) => void;
/**
 * Defines virtual (computed) property (getter/setter)
 */
export declare function Virtual(): (target: any, name: string, descriptor: PropertyDescriptor) => void;
/**
 * Defines index
 */
export declare function Index(): (target: any, name: string) => void;
/**
 * Defines pre-hoos
 *
 * @param {string} hookType
 * @param {string} actionType
 */
export declare function Hook(hookType: string, actionType: string): (target: any, name: string) => void;
